import { z } from "zod";
const jsErrorSchema = z.object({
    text: z.string(),
    location: z.object({
        column: z.number(),
        file: z.string(),
        length: z.number(),
        line: z.number(),
        lineText: z.string(),
        additionalLines: z.array(z.string()).optional(),
        namespace: z.string(),
        suggestion: z.string(),
    }),
});
export const errorsAndWarningsSchema = z.object({
    errors: z.array(jsErrorSchema).optional().readonly(),
    warnings: z.array(jsErrorSchema).optional().readonly(),
});
