import globalExternals from "@fal-works/esbuild-plugin-global-externals";
export function createServerBuildConfig(entryPoint) {
    return {
        entryPoints: [entryPoint],
        bundle: true,
        platform: "browser",
        format: "esm",
        plugins: [
            globalExternals({
                attio: {
                    varName: "ATTIO_ROOT_SDK",
                    type: "cjs",
                },
                "attio/server": {
                    varName: "ATTIO_SERVER_APP_SDK",
                    type: "cjs",
                },
            }),
        ],
    };
}
