import { promises as fs } from "fs";
import path from "path";
export async function findNodeModulesPath(pathToFile) {
    const cwd = process.cwd();
    let currentDir = cwd;
    while (currentDir !== "/") {
        const envFilePath = path.join(currentDir, "node_modules", "attio", ...pathToFile);
        try {
            await fs.access(envFilePath);
            return envFilePath;
        }
        catch {
        }
        currentDir = path.dirname(currentDir);
    }
    return null;
}
